import React from "react";
import { useNavigate } from "react-router-dom";
import "../Styles/HeroSection.css";

const events = [
  {
    id: 1,
    name: "Codethon",
    image: "https://via.placeholder.com/300x200?text=Codethon",
  },
  {
    id: 2,
    name: "Dancing",
    image: "https://via.placeholder.com/300x200?text=Dancing",
  },
  {
    id: 3,
    name: "Music Fest",
    image: "https://via.placeholder.com/300x200?text=Music+Fest",
  },
];

function HeroSection() {
  const navigate = useNavigate();

  const handleEventClick = (id) => {
    navigate(`/event/${id}`);
  };

  return (
    <div className="hero-section">
      {events.map((event) => (
        <div key={event.id} className="event-card" onClick={() => handleEventClick(event.id)}>
          <img src={event.image} alt={event.name} className="event-image" />
          <h3 className="event-title">{event.name}</h3>
        </div>
      ))}
    </div>
  );
}

export default HeroSection;
