import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "../Styles/EventDetails.css";

const GOOGLE_MAPS_API_KEY = "YOUR_GOOGLE_MAPS_API_KEY"; // 🔴 Replace this with your actual API key

const EventDetails = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [event, setEvent] = useState(null);
    const [showMap, setShowMap] = useState(false);

    useEffect(() => {
        axios
            .get(`http://localhost:5000/events/${id}`)
            .then((response) => setEvent(response.data))
            .catch((error) => console.error("Error fetching event details:", error));
    }, [id]);

    if (!event) return <p>Loading...</p>;

    const mapUrl = `https://www.google.com/maps/embed/v1/place?key=${GOOGLE_MAPS_API_KEY}&q=${encodeURIComponent(event.location)}`;

    return (
        <div className="event-details-container">
            <div className="event-banner">
                <img src={`http://localhost:5000/${event.image}`} alt={event.title} className="event-image" />
            </div>

            <div className="event-content">
                <h2 className="event-title">{event.title} <span className="share-icon">🔗</span></h2>
                <p className="event-description">{event.description}</p>
                <button className="read-more">Read More..</button>

                <div className="event-info">
                    <div className="event-date-time">
                        <p>💰 Price: {event.price || "Not Provided"}</p>
                        <p>📅 {event.date || "Not Provided"}</p>
                        <p>🕒 {event.time || "Not Provided"}</p>
                    </div>
                    <div className="event-location">
                        <p>📍 {event.location}</p>
                        <button 
                            className="map-link" 
                            onClick={() => setShowMap(!showMap)}
                        >
                            {showMap ? "Hide Map ▲" : "Show Map ▼"}
                        </button>
                    </div>
                </div>

                {showMap && (
                    <div className="map-container">
                        <iframe
                            title="Google Maps"
                            src={mapUrl}
                            width="100%"
                            height="300"
                            style={{ border: "0", borderRadius: "10px", marginTop: "10px" }}
                            allowFullScreen
                        ></iframe>
                    </div>
                )}

                <button 
                    onClick={() => navigate(`/payment?event=${id}`)} 
                    style={{ padding: "10px 20px", background: "#007bff", color: "#fff", border: "none", borderRadius: "5px", cursor: "pointer" }}
                >
                    🎟 Book Now
                </button>
            </div>

            <div className="chat-icon">💬</div>
        </div>
    );
};

export default EventDetails;
