import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // ✅ Import useNavigate
import './Styles/EventForm'
const EventForm = () => {
  const [eventData, setEventData] = useState({
    title: "",
    description: "",
    location: "",
    time: "",
    category: "",
    image: null,
  });

  const navigate = useNavigate(); // ✅ Initialize useNavigate

  const handleChange = (e) => {
    setEventData({ ...eventData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setEventData({ ...eventData, image: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    // ✅ Append all event details including "time"
    formData.append("title", eventData.title);
    formData.append("description", eventData.description);
    formData.append("date", eventData.date); // Ensure this is included
    formData.append("time", eventData.time); // ✅ Include this field
    formData.append("location", eventData.location);
    formData.append("category", eventData.category);
    if (eventData.image) {
        formData.append("image", eventData.image);
    }

    try {
        await axios.post("http://localhost:5000/events", formData, {
            headers: { "Content-Type": "multipart/form-data" },
        });

        alert("✅ Event Created Successfully!");
        navigate("/"); // Redirect to event list after success
    } catch (error) {
        console.error("Error:", error.response?.data || error.message);
        alert("❌ Failed to create event!");
    }
};


  return (
    <div style={{ maxWidth: "500px", margin: "auto", padding: "20px" }}>
      <h2>Create Event</h2>
      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
        <input type="text" name="title" placeholder="Title" onChange={handleChange} required />
        <textarea name="description" placeholder="Description" onChange={handleChange} required />
        <input type="text" name="location" placeholder="Location" onChange={handleChange} required />
        <input type="text" name="time" placeholder="Event Time" onChange={handleChange} required />
        <input type="text" name="category" placeholder="Category" onChange={handleChange} required />
        <input type="file" name="image" onChange={handleFileChange} required />
        <button type="submit" style={{ padding: "10px", backgroundColor: "#007bff", color: "#fff", border: "none", borderRadius: "5px", cursor: "pointer" }}>
          Create Event
        </button>
      </form>
    </div>
  );
};

export default EventForm;
