import React from 'react';

const EventCard = ({ event, onViewDetails }) => {
  return (
    <div className="event-card">
      <img src={`http://localhost:5000/${event.image}`} alt={event.title} className="event-image" />
      <div className="event-info">
        <h2 className="event-title">{event.title}</h2>
        <p className="event-description">{event.description.substring(0, 100)}...</p>
        <p className="event-date"><strong>Date:</strong> {event.date}</p>
        <p className="event-location"><strong>Location:</strong> {event.location}</p>
        <p className="event-category"><strong>Category:</strong> {event.category}</p>
        <div className="event-actions">
          <button className="register-btn">Register</button>
          <button className="details-btn" onClick={() => onViewDetails(event._id)}>View Details</button>
        </div>
      </div>
    </div>
  );
};

export default EventCard;
