import React, { useState } from "react";
import axios from "axios";

const EventForm = () => {
  const [eventData, setEventData] = useState({ title: "", description: "", date: "", location: "", image: null });

  const handleChange = (e) => setEventData({ ...eventData, [e.target.name]: e.target.value });
  const handleFileChange = (e) => setEventData({ ...eventData, image: e.target.files[0] });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.entries(eventData).forEach(([key, value]) => formData.append(key, value));

    await axios.post("http://localhost:5000/events", formData);
    alert("Event Created!");
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="title" placeholder="Title" onChange={handleChange} required />
      <input type="text" name="description" placeholder="Description" onChange={handleChange} required />
      <input type="date" name="date" onChange={handleChange} required />
      <input type="text" name="location" placeholder="Location" onChange={handleChange} required />
      <input type="file" name="image" onChange={handleFileChange} required />
      <button type="submit">Create Event</button>
    </form>
  );
};

export default EventForm;
