import React from "react";
import { useNavigate } from "react-router-dom";

const EventCard = ({ event }) => {
  const navigate = useNavigate();

  return (
    <div style={{ border: "1px solid #ddd", padding: "15px", width: "250px", background: "#fff" }}>
      <img src={`http://localhost:5000/${event.image}`} alt={event.title} width="100%" height="150px" />
      <h3>{event.title}</h3>
      <p>{event.date} - {event.location}</p>
      <button onClick={() => navigate(`/event/${event._id}`)}>View Details</button>
    </div>
  );
};

export default EventCard;
