import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const EventList = () => {
  const [events, setEvents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get("http://localhost:5000/events")
      .then((response) => setEvents(response.data))
      .catch((error) => console.error("Error fetching events:", error));
  }, []);

  return (
    <div style={{ maxWidth: "900px", margin: "auto", padding: "20px" }}>
      <button
        onClick={() => navigate("/create-event")}
        style={{
          padding: "10px 15px",
          backgroundColor: "#007bff",
          color: "#fff",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
          marginBottom: "20px",
          display: "block",
          width: "fit-content",
        }}
      >
        ➕ Create Event
      </button>

      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>📅 Event List</h2>

      {events.length > 0 ? (
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))",
            gap: "20px",
          }}
        >
          {events.map((event) => (
            <div
              key={event._id}
              style={{
                border: "1px solid #ddd",
                borderRadius: "10px",
                padding: "15px",
                boxShadow: "0px 4px 8px rgba(0,0,0,0.1)",
                backgroundColor: "#fff",
              }}
            >
              {/* ✅ Image Path Fixed */}
              {event.image && (
                <img
                  src={`http://localhost:5000/uploads/${event.image}`}
                  alt={event.title}
                  style={{
                    width: "100%",
                    height: "150px",
                    objectFit: "cover",
                    borderRadius: "8px",
                    marginBottom: "10px",
                  }}
                />
              )}

              <h3 style={{ color: "#333", marginBottom: "5px" }}>{event.title}</h3>
              <p style={{ color: "#777", fontSize: "14px" }}>
                <strong>Description:</strong> {event.description || "No description available"}
              </p>
              <p style={{ color: "#777", fontSize: "14px" }}>
                <strong>📅 Time:</strong> {event.date || "Not provided"}
              </p>
              <p style={{ color: "#777", fontSize: "14px" }}>
                <strong>📍 Location:</strong> {event.location || "Not specified"}
              </p>
              <p style={{ color: "#777", fontSize: "14px" }}>
                <strong>🏷 Category:</strong> {event.category || "General"}
              </p>
            </div>
          ))}
        </div>
      ) : (
        <p style={{ textAlign: "center", color: "#777" }}>No events found</p>
      )}
    </div>
  );
};

export default EventList;
