import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function HeroSection() {
    const [events, setEvents] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0);
    const navigate = useNavigate();

    useEffect(() => {
        axios
            .get("http://localhost:5000/events")
            .then((response) => {
                console.log("Fetched Events for Hero:", response.data);
                setEvents(response.data.slice(0, 3)); // ✅ Show first 3 events
            })
            .catch((error) => console.error("Error fetching events:", error));
    }, []);

    const scroll = (direction) => {
        if (events.length > 0) {
            setCurrentIndex((prevIndex) =>
                direction === "left"
                    ? (prevIndex - 1 + events.length) % events.length
                    : (prevIndex + 1) % events.length
            );
        }
    };

    return (
        <div className="mt-24 flex justify-center items-center relative h-80 bg-gray-100 text-center">
            {/* Scroll Left */}
            <button className="absolute left-4 text-3xl text-gray-700 hover:text-gray-900" onClick={() => scroll("left")}>
                ❮
            </button>

            {/* Hero Image */}
            <div className="w-4/5 max-w-2xl flex justify-center">
                {events.length > 0 ? (
                    <img
                        key={events[currentIndex]._id}
                        src={`http://localhost:5000/${events[currentIndex].image.replace(/\\/g, "/")}`}
                        alt={events[currentIndex].title}
                        className="w-full h-64 object-cover rounded-lg shadow-lg transition-opacity duration-500"
                        onClick={() => navigate(`/event/${events[currentIndex]._id}`)}
                    />
                ) : (
                    <p className="text-lg text-gray-600">Loading events...</p>
                )}
            </div>

            {/* Scroll Right */}
            <button className="absolute right-4 text-3xl text-gray-700 hover:text-gray-900" onClick={() => scroll("right")}>
                ❯
            </button>
        </div>
    );
}

export default HeroSection;
