import React, { useState } from 'react';
import axios from 'axios';

const EventForm = () => {
  const [eventData, setEventData] = useState({ title: '', description: '', location: '', time: '', category: '', image: null });

  const handleChange = (e) => {
    setEventData({ ...eventData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setEventData({ ...eventData, image: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    
    Object.keys(eventData).forEach((key) => {
      formData.append(key, eventData[key]);
    });
  
    try {
      const res = await axios.post("http://localhost:5000/events", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      console.log("Event Created:", res.data);
      alert("Event Created Successfully!");
    } catch (error) {
      console.error("Error:", error.response?.data || error.message);
    }
  };
  
  

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="title" placeholder="Title" onChange={handleChange} required />
      <input type="text" name="description" placeholder="Description" onChange={handleChange} required />
      <input type="text" name="location" placeholder="Location" onChange={handleChange} required />
      <input type="text" name="time" placeholder="Time" onChange={handleChange} required />
      <input type="text" name="category" placeholder="Category" onChange={handleChange} required />
      <input type="file" name="image" onChange={handleFileChange} required />
      <button type="submit">Create Event</button>
    </form>
  );
};
export default EventForm;
