import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../Styles/EventDetails.css";

const EventDetails = () => {
  const { id } = useParams();
  const [event, setEvent] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/events/${id}`) // Ensure backend matches this route
      .then((response) => setEvent(response.data))
      .catch((error) => console.error("Error fetching event details:", error));
  }, [id]);

  if (!event) return <p>Loading...</p>;

  return (
    <div className="event-details-container">
      <div className="event-banner">
        <img
          src={`http://localhost:5000/uploads/${event.image}`}
          alt={event.title}
          className="event-image"
        />
      </div>

      <div className="event-content">
        <h2 className="event-title">
          {event.title} <span className="share-icon">üîó</span>
        </h2>
        <p className="event-description">{event.description}</p>
        <a href="#" className="read-more">Read More..</a>

        <div className="event-info">
          <div className="event-date-time">
            <p>üìÖ {event.date || "Not Provided"}</p>
            <p>üïí {event.time || "Not Provided"}</p>
          </div>
          <div className="event-location">
            <p>üìç {event.location}</p>
            <span className="map-link">Show Maps ‚ñº</span>
          </div>
        </div>

        <button className="book-now-btn">Book Now</button>
      </div>

      <div className="chat-icon">üí¨</div>
    </div>
  );
};

export default EventDetails;
