import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import EventList from "./Components/EventList";
import EventDetails from "./Components/EventDetails";
import EventForm from "./Components/EventForm"; // Ensure you import EventForm
import Navbar from "./Components/Navbar";
import HeroSection from "./Components/HeroSection";

function App() {
  return (
    <Router>
      <Navbar />

      {/* ✅ Wrap HeroSection & EventList in a div for proper layout */}
      <div>
        <HeroSection />
        <EventList />
      </div>

      <Routes>
        {/* Event details page */}
        <Route path="/event/:id" element={<EventDetails />} />
      </Routes>

      {/* ✅ Conditionally render EventForm when the "Add" button is clicked */}
      <EventForm />
    </Router>
  );
}

export default App;
