import React, { useState, useContext } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import EventForm from "./Components/EventForm";
import EventList from "./Components/EventList";
import EventDetails from "./Components/EventDetails";
import Navbar from "./Components/Navbar";
import HeroSection from "./Components/HeroSection";
import Footer from "./Components/Footer";
import Payment from "./Components/Payment";
import Login from "./Components/Login";
import SavedEvents from "./Components/SavedEvents";
import { AuthProvider, AuthContext } from "./context/AuthContext";

function App() {
  const [selectedCategory, setSelectedCategory] = useState("");
  const [searchQuery, setSearchQuery] = useState("");

  return (
    <AuthProvider>
      <Router>
        <AppContent selectedCategory={selectedCategory} searchQuery={searchQuery} setSelectedCategory={setSelectedCategory} setSearchQuery={setSearchQuery} />
      </Router>
    </AuthProvider>
  );
}

function AppContent({ selectedCategory, searchQuery, setSelectedCategory, setSearchQuery }) {
  const { user } = useContext(AuthContext);

  return (
    <>
      <Navbar setSelectedCategory={setSelectedCategory} setSearchQuery={setSearchQuery} />
      <Routes>
        {/* Redirect to login if user is not logged in */}
        <Route path="/" element={user ? <><HeroSection /><EventList selectedCategory={selectedCategory} searchQuery={searchQuery} /></> : <Navigate to="/login" />} />
        <Route path="/login" element={<Login />} />
        <Route path="/saved-events" element={user ? <SavedEvents /> : <Navigate to="/login" />} />
        <Route path="/create-event" element={user ? <EventForm /> : <Navigate to="/login" />} />
        <Route path="/event/:id" element={user ? <EventDetails /> : <Navigate to="/login" />} />
        <Route path="/payment" element={user ? <Payment /> : <Navigate to="/login" />} />
      </Routes>
      <Footer />
    </>
  );
}

export default App;
