import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const GOOGLE_MAPS_API_KEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

const EventDetails = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [event, setEvent] = useState(null);
    const [error, setError] = useState(null);
    const [expanded, setExpanded] = useState(false);
    const [showMap, setShowMap] = useState(false);

    useEffect(() => {
        axios
            .get(`http://localhost:5000/events/${id}`)
            .then((response) => setEvent(response.data))
            .catch((error) => {
                console.error("Error fetching event details:", error);
                setError("Failed to load event details. Please try again later.");
            });
    }, [id]);

    if (error) return <p className="text-red-500 text-center mt-4">{error}</p>;
    if (!event) return <p className="text-gray-600 text-center mt-4">Loading event details...</p>;

    return (
        <div className="max-w-4xl mx-auto p-6 bg-gray-100 rounded-lg shadow-md">
            {/* Event Banner */}
            <div className="overflow-hidden rounded-lg shadow-lg">
                {event.image ? (
                    <img 
                        src={`http://localhost:5000/${event.image.replace(/\\/g, "/")}`} 
                        alt={event.title} 
                        className="w-full h-64 object-cover" 
                    />
                ) : (
                    <p className="text-center text-gray-500 py-4">No Image Available</p>
                )}
            </div>

            {/* Event Content */}
            <div className="bg-white rounded-lg shadow-md mt-6 p-6">
                <h2 className="text-2xl font-bold text-gray-800 flex items-center">
                    {event.title} <span className="ml-2">üîó</span>
                </h2>
                <p className="text-gray-600 mt-2">
                    {expanded ? event.description : `${event.description.substring(0, 100)}...`}
                </p>
                <button 
                    className="mt-2 text-blue-500 hover:text-blue-700 underline"
                    onClick={() => setExpanded(!expanded)}
                >
                    {expanded ? "Read Less" : "Read More"}
                </button>

                {/* Event Info */}
                <div className="mt-4 bg-gray-100 p-4 rounded-lg">
                    <p className="text-gray-800"><span className="font-semibold">üí∞ Price:</span> {event.price || "Not Provided"}</p>
                    <p className="text-gray-800"><span className="font-semibold">üìÖ Date:</span> {event.date || "Not Provided"}</p>
                    <p className="text-gray-800"><span className="font-semibold">üïí Time:</span> {event.time || "Not Provided"}</p>
                </div>

                {/* Event Location */}
                <div className="mt-4">
                    <p className="text-gray-800 font-semibold">üìç Location: {event.location}</p>
                    {event.location && (
                        <>
                            <button 
                                className="mt-2 text-blue-600 hover:text-blue-800 underline"
                                onClick={() => setShowMap(!showMap)}
                            >
                                {showMap ? "Hide Map ‚ñ≤" : "Show Map ‚ñº"}
                            </button>
                            {showMap && GOOGLE_MAPS_API_KEY ? (
                                <iframe
                                    title="Google Map"
                                    className="w-full h-72 rounded-lg mt-3"
                                    frameBorder="0"
                                    src={`https://www.google.com/maps/embed/v1/place?key=${GOOGLE_MAPS_API_KEY}&q=${encodeURIComponent(event.location)}`}
                                    allowFullScreen
                                ></iframe>
                            ) : showMap ? (
                                <p className="text-red-500 mt-2">‚ö†Ô∏è Google Maps API Key is missing or invalid.</p>
                            ) : null}
                        </>
                    )}
                </div>

                {/* Booking Button */}
                <button 
                    onClick={() => navigate(`/payment?event=${id}`)} 
                    className="mt-6 w-full bg-blue-600 text-white py-3 rounded-lg shadow-md hover:bg-blue-700 transition"
                >
                    üéü Book Now
                </button>
            </div>

            {/* Chat Icon */}
            <div className="fixed bottom-6 right-6 bg-blue-600 text-white p-4 rounded-full shadow-lg cursor-pointer hover:bg-blue-700 transition">
                üí¨
            </div>
        </div>
    );
};

export default EventDetails;
