import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import './EventLlist.css' // Import CSS file

const EventList = () => {
    const [events, setEvents] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        axios
            .get("http://localhost:5000/events")
            .then((response) => {
                console.log("Fetched Events:", response.data);
                setEvents(response.data);
            })
            .catch((error) => console.error("Error fetching events:", error));
    }, []);

    return (
        <div className="event-container">
            <button className="create-btn" onClick={() => navigate("/create-event")}>
                ‚ûï Create Event
            </button>

            <h2 className="title">üìÖ Event List</h2>

            {events.length > 0 ? (
                <div className="event-grid">
                    {events.map((event) => (
                        <div key={event._id} className="event-card">
                            {event.image && (
                                <img
                                    src={`http://localhost:5000/${event.image.replace(/\\/g, "/")}`} // ‚úÖ Fix Windows paths
                                    alt={event.title}
                                    className="event-img"
                                />
                            )}

                            <h3 className="event-title">{event.title}</h3>
                            <p className="event-desc"><strong>Description:</strong> {event.description}</p>
                            <p className="event-info"><strong>üìÖ Time:</strong> {event.time || "Not provided"}</p>
                            <p className="event-info"><strong>üìç Location:</strong> {event.location}</p>
                            <p className="event-category"><strong>üè∑ Category:</strong> {event.category || "General"}</p>
                        </div>
                    ))}
                </div>
            ) : (
                <p className="no-events">No events found</p>
            )}
        </div>
    );
};

export default EventList;
