import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import StripeCheckout from "react-stripe-checkout";

const Payment = () => {
    const location = useLocation();
    const params = new URLSearchParams(location.search);
    const eventId = params.get("event");

    const [event, setEvent] = useState(null);
    const [tickets, setTickets] = useState(1);

    useEffect(() => {
        if (eventId) {
            axios.get(`http://localhost:5000/events/${eventId}`)
                .then((response) => setEvent(response.data))
                .catch((error) => console.error("Error fetching event:", error));
        }
    }, [eventId]);

    if (!event) return <p>Loading payment details...</p>;

    const totalAmount = event.price * tickets + 50; // Platform fee

    const handlePayment = async (token) => {
        try {
            const response = await axios.post("http://localhost:5000/payment", {
                amount: totalAmount,
                token
            });

            if (response.data.success) {
                alert("Payment Successful! ðŸŽ‰");
            }
        } catch (error) {
            alert("Payment Failed: " + error.message);
        }
    };

    return (
        <div style={{ padding: "20px", maxWidth: "900px", margin: "auto" }}>
            <h2>Payment</h2>

            <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr 1fr", gap: "20px" }}>
                <div>
                    <h3>Details</h3>
                    <label>Number of Tickets:</label>
                    <input 
                        type="number" 
                        value={tickets} 
                        min="1" 
                        onChange={(e) => setTickets(parseInt(e.target.value))} 
                        style={{ width: "50px", margin: "10px 0" }}
                    />
                    <input type="text" placeholder="Name" />
                    <br />
                    <input type="radio" name="gender" value="Male" /> Male
                    <input type="radio" name="gender" value="Female" /> Female
                    <input type="radio" name="gender" value="Other" /> Other
                    <br />
                    <input type="text" placeholder="Enter Phone Number" />
                </div>

                <div>
                    <h3>Order Summary</h3>
                    <p><strong>{event.title}</strong></p>
                    <p>â‚¹{event.price} x {tickets} Tickets</p>
                    <p>Platform Fees: â‚¹50</p>
                    <h3>Total: â‚¹{totalAmount}</h3>
                </div>

                <div>
                    <h3>Payment</h3>
                    <StripeCheckout
                        stripeKey="pk_test_your_stripe_public_key"
                        token={handlePayment}
                        amount={totalAmount * 100}
                        name="Event Ticket"
                        currency="INR"
                    >
                        <button style={{ background: "#007bff", color: "white", padding: "10px", cursor: "pointer" }}>
                            Pay INR {totalAmount}
                        </button>
                    </StripeCheckout>
                </div>
            </div>
        </div>
    );
};

export default Payment;
