import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../Styles/HeroSection.css";

function HeroSection() {
  const [event, setEvent] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get("http://localhost:5000/events/latest")
      .then((response) => setEvent(response.data))
      .catch((error) => console.error("Error fetching latest event:", error));
  }, []);

  if (!event) return <p>Loading event...</p>;

  return (
    <div className="hero-section" onClick={() => navigate(`/event/${event._id}`)}>
      <img src={`http://localhost:5000/${event.image}`} alt={event.title} className="hero-image" />
    </div>
  );
}

export default HeroSection;
