import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../Styles/HeroSection.css";

function HeroSection() {
  const [events, setEvents] = useState([]);
  const navigate = useNavigate();
  const scrollRef = useRef(null);

  useEffect(() => {
    axios
      .get("http://localhost:5000/events") // ✅ Fixed API URL
      .then((response) => setEvents(response.data))
      .catch((error) => console.error("Error fetching events:", error));
  }, []);

  const scroll = (direction) => {
    if (scrollRef.current) {
      const scrollAmount = 300; 
      scrollRef.current.scrollBy({ left: direction === "left" ? -scrollAmount : scrollAmount, behavior: "smooth" });
    }
  };

  return (
    <div className="hero-section">
      <button className="scroll-btn left-btn" onClick={() => scroll("left")}>❮</button>
      <div className="hero-container" ref={scrollRef}>
        {events.length > 0 ? (
          events.slice(0, 3).map((event) => (  // ✅ Only show 3 images
            <img
              key={event._id}
              src={`http://localhost:5000/${event.image.replace(/\\/g, "/")}`} // ✅ Fixed Image Path
              alt={event.title}
              className="hero-image"
              onClick={() => navigate(`/event/${event._id}`)}
            />
          ))
        ) : (
          <p className="loading-text">Loading events...</p>
        )}
      </div>
      <button className="scroll-btn right-btn" onClick={() => scroll("right")}>❯</button>
    </div>
  );
}

export default HeroSection;
