import React from "react";
import { useNavigate } from "react-router-dom";
import "../Styles/HeroSection.css";

const eventImages = [
  { id: 1, src: "/images/codethon.jpg", alt: "Codethon Event" },
  { id: 2, src: "/images/dancing.jpg", alt: "Dancing Event" },
  { id: 3, src: "/images/music.jpg", alt: "Music Fest" },
];

function HeroSection() {
  const navigate = useNavigate();

  return (
    <div className="hero-section">
      {eventImages.map((event) => (
        <img
          key={event.id}
          src={event.src}
          alt={event.alt}
          className="hero-image"
          onClick={() => navigate(`/event/${event.id}`)} // Navigate on click
        />
      ))}
    </div>
  );
}

export default HeroSection;
