import React, { useEffect, useState } from "react";
import axios from "axios"; // For API requests
import "./StylesEventDetails.css";
import { FaCalendarAlt, FaMapMarkerAlt } from "react-icons/fa";

const EventDetails = () => {
  const [event, setEvent] = useState(null); // Store event data
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch event data from the backend
    axios
      .get("http://localhost:5000/api/events/1") // Update API URL as needed
      .then((response) => {
        setEvent(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching event:", error);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading event details...</p>;
  if (!event) return <p>No event found.</p>;

  return (
    <div className="event-details-container">
      {/* Event Banner */}
      <div className="event-banner">
        <img src={event.imageUrl} alt={event.title} className="event-image" />
      </div>

      {/* Event Content */}
      <div className="event-content">
        {/* Left Section - Title & Description */}
        <div className="event-left">
          <h2 className="event-title">
            {event.title} <span className="share-icon">ðŸ”—</span>
          </h2>
          <p className="event-description">{event.description}</p>
          <a href="#" className="read-more">Read More..</a>
        </div>

        {/* Right Section - Event Info */}
        <div className="event-right">
          <div className="event-info">
            <FaCalendarAlt />
            <div className="event-text">
              <p>{new Date(event.date).toDateString()}</p>
              <p>{event.time}</p>
            </div>
          </div>

          <div className="event-info">
            <FaMapMarkerAlt />
            <div className="event-text">
              <p>{event.venue}</p>
              <p>{event.address}</p>
              <span className="map-link">Show Maps</span>
            </div>
          </div>
        </div>
      </div>

      {/* Book Now Button */}
      <button className="book-now-btn">Book Now</button>

      {/* Floating Chat Icon */}
      <div className="chat-icon">ðŸ’¬</div>
    </div>
  );
};

export default EventDetails;
