import React, { useState } from "react";
import axios from "axios";

const EventForm = () => {
  const [eventData, setEventData] = useState({ title: "", description: "", date: "", location: "", image: null });

  const handleChange = (e) => {
    setEventData({ ...eventData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setEventData({ ...eventData, image: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", eventData.title);
    formData.append("description", eventData.description);
    formData.append("date", eventData.date);
    formData.append("location", eventData.location);
    formData.append("image", eventData.image);

    await axios.post("http://localhost:5000/events", formData);
    alert("Event Created!");
  };

  return (
    <form onSubmit={handleSubmit} style={{ textAlign: "center", padding: "20px" }}>
      <input type="text" name="title" placeholder="Title" onChange={handleChange} required /><br />
      <input type="text" name="description" placeholder="Description" onChange={handleChange} required /><br />
      <input type="date" name="date" onChange={handleChange} required /><br />
      <input type="text" name="location" placeholder="Location" onChange={handleChange} required /><br />
      <input type="file" name="image" onChange={handleFileChange} required /><br />
      <button type="submit">Create Event</button>
    </form>
  );
};

export default EventForm;
