import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const GOOGLE_MAPS_API_KEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

const EventDetails = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [event, setEvent] = useState(null);
    const [error, setError] = useState(null);
    const [expanded, setExpanded] = useState(false);
    const [showMap, setShowMap] = useState(false);

    useEffect(() => {
        axios
            .get(`http://localhost:5000/events/${id}`)
            .then((response) => setEvent(response.data))
            .catch((error) => {
                console.error("Error fetching event details:", error);
                setError("Failed to load event details. Please try again later.");
            });
    }, [id]);

    if (error) return <p className="text-red-500 text-center mt-4">{error}</p>;
    if (!event) return <p className="text-gray-500 text-center mt-4">Loading event details...</p>;

    return (
        <div className="max-w-4xl mx-auto p-6 bg-white shadow-lg rounded-lg mt-6">
            {/* Event Banner */}
            <div className="rounded-lg overflow-hidden shadow-md">
                {event.image ? (
                    <img
                        src={`http://localhost:5000/${event.image.replace(/\\/g, "/")}`}
                        alt={event.title}
                        className="w-full h-64 object-cover"
                    />
                ) : (
                    <p className="text-center text-gray-500 py-10">No Image Available</p>
                )}
            </div>

            {/* Event Details */}
            <div className="mt-6">
                <h2 className="text-2xl font-bold text-blue-600 flex items-center">
                    {event.title} <span className="ml-2 text-gray-400 cursor-pointer">üîó</span>
                </h2>
                <p className="text-gray-700 mt-2">
                    {expanded ? event.description : `${event.description.substring(0, 100)}...`}
                </p>
                <button
                    className="text-blue-600 font-semibold mt-2 hover:underline"
                    onClick={() => setExpanded(!expanded)}
                >
                    {expanded ? "Read Less" : "Read More"}
                </button>

                {/* Event Info */}
                <div className="bg-gray-100 p-4 rounded-lg mt-4">
                    <p className="flex items-center text-gray-800"><span className="mr-2">üí∞</span>Price: {event.price || "Not Provided"}</p>
                    <p className="flex items-center text-gray-800"><span className="mr-2">üìÖ</span>Date: {event.date || "Not Provided"}</p>
                    <p className="flex items-center text-gray-800"><span className="mr-2">üïí</span>Time: {event.time || "Not Provided"}</p>
                    <p className="flex items-center text-gray-800"><span className="mr-2">üìç</span>Location: {event.location}</p>
                    {event.location && (
                        <>
                            <button
                                className="text-blue-600 font-semibold hover:underline mt-2"
                                onClick={() => setShowMap(!showMap)}
                            >
                                {showMap ? "Hide Map ‚ñ≤" : "Show Map ‚ñº"}
                            </button>
                            {showMap && GOOGLE_MAPS_API_KEY ? (
                                <iframe
                                    title="Google Map"
                                    width="100%"
                                    height="300"
                                    frameBorder="0"
                                    className="rounded-lg mt-2"
                                    src={`https://www.google.com/maps/embed/v1/place?key=${GOOGLE_MAPS_API_KEY}&q=${encodeURIComponent(event.location)}`}
                                    allowFullScreen
                                ></iframe>
                            ) : showMap ? (
                                <p className="text-red-500 mt-2">‚ö†Ô∏è Google Maps API Key is missing or invalid.</p>
                            ) : null}
                        </>
                    )}
                </div>

                {/* Booking Button */}
                <button
                    onClick={() => navigate(`/payment?event=${id}`)}
                    className="w-full bg-blue-600 text-white text-lg font-semibold py-3 rounded-lg mt-4 hover:bg-blue-700"
                >
                    üéü Book Now
                </button>
            </div>

            {/* Chat Icon */}
            <div className="fixed bottom-6 right-6 bg-blue-600 text-white p-4 rounded-full cursor-pointer shadow-md hover:bg-blue-700">
                üí¨
            </div>
        </div>
    );
};

export default EventDetails;
