import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const EventList = () => {
    const [events, setEvents] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        axios
            .get("http://localhost:5000/events")
            .then((response) => {
                console.log("Fetched Events:", response.data); // Debugging
                setEvents(response.data);
            })
            .catch((error) => console.error("Error fetching events:", error));
    }, []);

    return (
        <div style={{ maxWidth: "900px", margin: "auto", padding: "20px" }}>
            <h2>📅 Event List</h2>

            {events.length > 0 ? (
                <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))", gap: "20px" }}>
                    {events.map((event) => (
                        <div 
                            key={event._id} 
                            style={{ border: "1px solid #ddd", padding: "15px", borderRadius: "10px" }}
                        >
                            {/* Clickable image to navigate to event details */}
                            {event.image && (
                                <img
                                    src={`http://localhost:5000/${event.image.replace(/\\/g, "/")}`}
                                    alt={event.title}
                                    style={{marginLeft: "6rem", width: "200px", height: "150px", objectFit: "cover", borderRadius: "5px", cursor: "pointer" }}
                                    onClick={() => navigate(`/event/${event._id}`)} // ✅ Navigate on click
                                />
                            )}

                             <h3 style={{textAlign: "center", textEmphasisColor: "blue"}}><strong>Title: </strong>{event.title}</h3>
                            <p style={{textAlign: "center"}}><strong>Description:</strong> {event.description}</p>
                            <p style={{textAlign: "center"}}><strong>📅 Time:</strong> {event.time ? event.time : "Not provided"}</p> 
                            <p style={{textAlign: "center"}}><strong>📍 Location:</strong> {event.location}</p>
                            <p style={{textAlign: "center"}}><strong>🏷 Category:</strong> {event.category || "General"}</p>

                            <button 
                                onClick={() =>  navigate(`/event/${event._id}`)} 
                                style={{marginLeft: "9rem", padding: "8px 12px", cursor: "pointer", background: "#007bff", color: "#fff", border: "none", borderRadius: "5px" }}
                            >
                                🔍 View Details
                            </button>
                        </div>
                    ))}
                </div>
            ) : (
                <p>No events found</p>
            )}
        </div>
    );
};

export default EventList;
