import React from "react";
import { useNavigate } from "react-router-dom";

const EventCard = ({ event }) => {
  const navigate = useNavigate();

  return (
    <div onClick={() => navigate(`/event/${event._id}`)} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px", cursor: "pointer" }}>
      <h3>{event.title}</h3>
      <p>{event.date} - {event.location}</p>
      <img src={`http://localhost:5000/${event.image}`} alt={event.title} width="100" />
    </div>
  );
};

export default EventCard;
