import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const EventList = () => {
    const [events, setEvents] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        axios
            .get("http://localhost:5000/events")
            .then((response) => {
                console.log("Fetched Events:", response.data); // Debugging
                setEvents(response.data);
            })
            .catch((error) => console.error("Error fetching events:", error));
    }, []);

    return (
        <div style={{ maxWidth: "900px", margin: "auto", padding: "20px" }}>
            <button
                onClick={() => navigate("/create-event")}
                style={{
                    padding: "10px 15px",
                    backgroundColor: "#007bff",
                    color: "#fff",
                    border: "none",
                    borderRadius: "5px",
                    cursor: "pointer",
                    marginBottom: "20px",
                }}
            >
                ‚ûï Create Event
            </button>

            <h2>üìÖ Event List</h2>

            {events.length > 0 ? (
                <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))", gap: "20px" }}>
                    {events.map((event) => (
                        <div key={event._id} style={{ border: "1px solid #ddd", padding: "15px", borderRadius: "10px" }}>
                            {event.image && (
                                <img
                                    src={`http://localhost:5000/${event.image}`} // ‚úÖ Correct path
                                    alt={event.title}
                                    style={{ width: "200px", height: "150px", objectFit: "cover", borderRadius: "5px" }}
                                />
                            )}

                            <h3>{event.title}</h3>
                            <p><strong>Description:</strong> {event.description}</p>
                            <p><strong>üìÖ Time:</strong> {event.time || "Not provided"}</p>
                            <p><strong>üìç Location:</strong> {event.location}</p>
                            <p><strong>üè∑ Category:</strong> {event.category || "General"}</p>
                        </div>
                    ))}
                </div>
            ) : (
                <p>No events found</p>
            )}
        </div>
    );
};

export default EventList;
