import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const EventList = () => {
  const [events, setEvents] = useState([]);
  const navigate = useNavigate(); 

  useEffect(() => {
    axios
      .get("http://localhost:5000/events")
      .then((response) => setEvents(response.data))
      .catch((error) => console.error("Error fetching events:", error));
  }, []);

  return (
    <div style={{ maxWidth: "700px", margin: "auto", padding: "20px" }}>
      {/* âœ… Button added here (before the event list) */}
      <button
        onClick={() => navigate("/create-event")} // âœ… Navigate to EventForm
        style={{
          padding: "10px 15px",
          backgroundColor: "#007bff",
          color: "#fff",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
          marginBottom: "20px",
        }}
      >
        âž• Create Event
      </button>

      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>ðŸ“… Event List</h2>

      {events.length > 0 ? (
        events.map((event) => (
          <div
            key={event._id}
            style={{
              border: "1px solid #ddd",
              borderRadius: "10px",
              padding: "15px",
              marginBottom: "20px",
              boxShadow: "0px 4px 8px rgba(0,0,0,0.1)",
              backgroundColor: "#fff",
            }}
          >
            <h3 style={{ color: "#333", marginBottom: "10px" }}>{event.title}</h3>
            <p style={{ color: "#555", fontSize: "14px" }}>
              <strong>Description:</strong> {event.description}
            </p>
            <p style={{ color: "#555", fontSize: "14px" }}>
              <strong>Time:</strong> {event.time || "N/A"}
            </p>
            <p style={{ color: "#555", fontSize: "14px" }}>
              <strong>Location:</strong> {event.location || "N/A"}
            </p>
            <p style={{ color: "#555", fontSize: "14px" }}>
              <strong>Category:</strong> {event.category || "N/A"}
            </p>
            {event.image && (
              <img
                src={`http://localhost:5000/${event.image.replace(/\\/g, "/")}`}
                alt={event.title}
                style={{
                  width: "100%",
                  maxHeight: "200px",
                  objectFit: "cover",
                  borderRadius: "8px",
                  marginTop: "10px",
                }}
              />
            )}
          </div>
        ))
      ) : (
        <p style={{ textAlign: "center", color: "#777" }}>No events found</p>
      )}
    </div>
  );
};

export default EventList;
