import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../Styles/EventDetails.css"; // Create a separate CSS file for styling

const EventDetails = () => {
    const { id } = useParams();
    const [event, setEvent] = useState(null);

    useEffect(() => {
        axios
            .get(`http://localhost:5000/events/${id}`)
            .then((response) => setEvent(response.data))
            .catch((error) => console.error("Error fetching event details:", error));
    }, [id]);

    if (!event) return <p>Loading...</p>;

    return (
        <div className="event-details-container">
            <h2>{event.title}</h2>
            {event.image && <img src={`http://localhost:5000/${event.image}`} alt={event.title} className="event-details-image" />}
            <p><strong>Description:</strong> {event.description}</p>
            <p><strong>üìÖ Time:</strong> {event.time || "Not provided"}</p>
            <p><strong>üìç Location:</strong> {event.location}</p>
            <p><strong>üè∑ Category:</strong> {event.category || "General"}</p>
        </div>
    );
};

export default EventDetails;
