import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate, useLocation } from "react-router-dom";

const EventList = () => {
    const [events, setEvents] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");
    const navigate = useNavigate();
    const location = useLocation();

    // Get category from URL query parameters
    const searchParams = new URLSearchParams(location.search);
    const selectedCategory = searchParams.get("category");

    useEffect(() => {
        axios.get("http://localhost:5000/events")
            .then((response) => {
                console.log("Fetched Events:", response.data);
                let filteredEvents = response.data;

                // Filter by category if selected
                if (selectedCategory) {
                    filteredEvents = filteredEvents.filter(event => event.category === selectedCategory);
                }

                // Filter by search query if present
                if (searchQuery.trim()) {
                    filteredEvents = filteredEvents.filter(event => 
                        event.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                        event.location.toLowerCase().includes(searchQuery.toLowerCase())
                    );
                }

                setEvents(filteredEvents);
            })
            .catch((error) => console.error("Error fetching events:", error));
    }, [selectedCategory, searchQuery]);

    return (
        <div style={{ maxWidth: "900px", margin: "auto", padding: "20px" }}>
            <h2 style={{ textAlign: "center", color: "green" }}>
                ......{selectedCategory ? `${selectedCategory} Events` : "All Events"}......
            </h2>
            
            {/* Search Bar */}
            <input 
                type="text" 
                placeholder="Search events by title or location..." 
                value={searchQuery} 
                onChange={(e) => setSearchQuery(e.target.value)} 
                style={{ 
                    width: "100%", 
                    padding: "10px", 
                    marginBottom: "20px", 
                    borderRadius: "5px", 
                    border: "1px solid #ccc" 
                }}
            />
            
            {events.length > 0 ? (
                <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))", gap: "20px" }}>
                    {events.map((event) => (
                        <div 
                            key={event._id} 
                            style={{ border: "1px solid #ddd", padding: "15px", borderRadius: "10px" }}
                        >
                            {/* Clickable image to navigate to event details */}
                            {event.image && (
                                <img
                                    src={`http://localhost:5000/${event.image.replace(/\\/g, "/")}`}
                                    alt={event.title}
                                    style={{
                                        width: "100%", 
                                        height: "150px", 
                                        objectFit: "cover", 
                                        borderRadius: "5px", 
                                        cursor: "pointer"
                                    }}
                                    onClick={() => navigate(`/event/${event._id}`)}
                                />
                            )}

                            <h3 style={{ textAlign: "center", color: "blue" }}>
                                <strong>Title: </strong>{event.title}
                            </h3>
                            <p style={{ textAlign: "center" }}><strong>Description:</strong> {event.description}</p>
                            <p style={{ textAlign: "center" }}>📅 Date: {event.date ? event.date : "TBD"}</p>
                            <p style={{ textAlign: "center" }}>💰 Price: ₹{event.price ? event.price : "Free"}</p>
                            <p style={{ textAlign: "center" }}><strong>⏲️ Time:</strong> {event.time}</p> 
                            <p style={{ textAlign: "center" }}><strong>📍 Location:</strong> {event.location}</p>
                            <p style={{ textAlign: "center" }}><strong>🏷 Category:</strong> {event.category || "General"}</p>

                            <button 
                                onClick={() => navigate(`/event/${event._id}`)} 
                                style={{
                                    display: "block",
                                    margin: "10px auto",
                                    padding: "8px 12px",
                                    cursor: "pointer",
                                    background: "#007bff",
                                    color: "#fff",
                                    border: "none",
                                    borderRadius: "5px"
                                }}
                            >
                                🔍 View Details
                            </button>
                        </div>
                    ))}
                </div>
            ) : (
                <p style={{ textAlign: "center", fontSize: "18px", fontWeight: "bold" }}>
                    No events found
                </p>
            )}
        </div>
    );
};

export default EventList;
